import React from "react";

const conteudos = [
  {
    conteudo: 'High Order Functions',
    bloco: 8,
    status: 'Aprendido'
  },
  {
    conteudo: 'Composicao de Componentes',
    bloco: 11,
    status: 'Aprendendo',
  },
  {
    conteudo: 'Composicao de Estados',
    bloco: 12,
    status: 'Aprenderei'
  },
  {
    conteudo: 'Redux',
    bloco: 16,
    status: 'Aprenderei'
  },
];

const nomeDoConteudo = conteudos.map((nome) => nome.conteudo);
const statusInfo = conteudos.map((info) => info.status);
const blocoInfo = conteudos.map((info) => info.bloco);

class Content extends React.Component {
  render() {
    return (
      <div>
        <p>O conteúdo é: `${nomeDoConteudo}`</p>
        <p>Status: `${statusInfo}`</p>
        <p>Bloco: `${blocoInfo}`</p>
      </div>
    )
  }
}

export default Content;
